(function(){

  var rework = require('broccoli-rework-single'),
  imprt  = require('rework-import'),
  path = require("path"),
  dist_dir = path.join(__dirname,'../../../');

  module.exports = {
    ngReworkCss: function(){
      var app_css = path.join(dist_dir,'/app/styles');
      return rework(app_css,'./app.css','./app.css',{
        use: function(css) {
          css.use(imprt());
        }
      });
    }
  }
})();
